/* minimal placeholder for axe-core; in real runs axe is loaded from dependency */
// Minimal axe stub for local testing. Supports both callback-style and Promise-style usage.
(function(){
  if(!window.axe){
    window.axe = {};
    window.axe.run = function(){
      var results = { violations: [] };
      var last = arguments[arguments.length - 1];
      // If last argument is a callback function, call it (callback-style)
      if (typeof last === 'function') {
        try {
          setTimeout(function(){ last(null, results); }, 0);
        } catch (e) {
          try { last(e); } catch(ignore){}
        }
        return;
      }
      // Otherwise return a Promise (Promise-style)
      return Promise.resolve(results);
    };
  }
})();